function safeGet(url, retry = 3) {
    $httpClient.get(url, function(error, response, data) {
        if (error || !data) {
            console.log(`请求失败，剩余重试次数: ${retry - 1}`);
            if (retry > 1) {
                safeGet(url, retry - 1);
            } else {
                $done({ body: JSON.stringify({ error: 'request_failed' }) });
            }
        } else {
            $done({ body: data });
        }
    });
}




